import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

const UserListForm = () => {
    // const users = useSelector(async (state) => await state.userReducer.userState)
    // alert(`0000${users}`) 
    const [users, setUsers] = useState()
    useEffect(() => {
        users = useSelector(async (state) => await state.userReducer.userState)
      }, [users])
    return (<table border='1px' style={{textAlign:'center'}}>
    <thead>
    <tr><th>사용자번호</th>
        <th>사용자아이디</th>
        <th>이름</th>
        <th>이메일</th></tr>
    </thead>
    <tbody>
    {users.map((user)=>(
        <tr><td>{user.userId}</td>
        <td>{user.username}</td>
        <td>{user.name}</td>
        <td>{user.email}</td></tr>
    ))}
    </tbody>
    </table>)
}

export default UserListForm
