import axios from 'axios';
import React, { useState } from 'react';


export default function UserAdd() {
    const SERVER = 'http://localhost:8080'
    const [join, setJoin] = useState({
        username:'', password:'', email:'', name:''
    })
    const {username, password, email, name} = join
    const headers = {
        'Content-Type': 'application/json',
        'Authorization': 'JWT fefege..'
    }

    const handleChange = e => {
        const { value, name } = e.target
        setJoin({
            ...join,
            [name]:  value
        })
    }
    const userJoin = join => axios.post(`${SERVER}/users`, JSON.stringify(join), {headers})
    const handleSubmit = e => {
        e.preventDefault()
        const joinRequest = {...join}
        userJoin(joinRequest)
        .then(res =>{
            alert('회원가입 성공')
        })
        .catch(err => {
            console.log(`회원가입 실패 : ${err}`)
        });
    }

    return (
        <div>
            <h1>회원 가입을 환영합니다</h1>
            <form onSubmit={handleChange} method="POST">
                <ul>
                    <li>
                        <label>
                            아이디: <input type="text" id="username" name="username" size="10" minlength="4" maxlength="15"/>
                        </label>
                        <small>4~15자리 이내의 영문과 숫자</small>
                    </li>
                    <li>
                        <label>
                            이메일: <input type="email" id="user_email"/>
                        </label>
                    </li>
                    <li>
                        <label>
                            비밀번호: <input type="password" id="user_pwd"/>
                        </label>
                    </li>
                    <li>
                        <label>
                            비밀번호 확인: <input type="password" id="check_pwd"/>
                        </label>
                    </li>
                    <li>
                        <input type="submit" value="회원가입"/>
                    </li>
                </ul>
            </form>
        </div>
      );
    }
