import React, { useState } from 'react';
import axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';

export default function UserLogin() {
  const SERVER = 'http://localhost:8080'
  const [login, setLogin] = useState({})
  const {username, password} = login

  const handleChange = e => {
    const {value, name} = e.target
    setLogin({
      ...login,
      [name] : value
    })
  }
  const handleClick = e =>{
    e.preventDefault()
    const loginRequest = {username, password}
    // userLogin(loginRequest)
    alert('로그인정보'+JSON.stringify(loginRequest))
    let form = new FormData()
    form.append("username", login.username)
    form.append("password", login.password)
    axios.post(`${SERVER}/users/login`, form)
    .then(res => {
      alert('로그인 성공, '+res)
    })
    .catch(err => {
      alert('로그인 실패')
    })
  }
  const userLogin = loginRequest => axios.post(`${SERVER}/users/login`, loginRequest)
  return (
    <div>
      <h1>유저 로그인</h1>
      <form method="POST">
        <ul>
          <li><label for="id">아이디</label>
          <input type="text" id="username"
              name="username" value={username} onChange={handleChange}/></li>
          <li><label for="pw">비밀번호</label>
          <input type="password" id="password" name="password" onChange={handleChange}/></li>
          <li><input type="button" title="로그인" value="로그인" onClick={handleClick}/></li>
        </ul>
      </form>
    </div>
  );
}
